package com.example.awash.Config.Serviceimport com.example.awash.Config.Models.Vehiculeimport retrofit2.Callimport retrofit2.http.*interface VehiculeService {    @GET("vehicules")    fun getVehicules(@Header("Authorization") token: String): Call<List<Vehicule>>    @POST("vehicules")    fun createVehicule(@Header("Authorization") token: String, @Body vehicule: Vehicule): Call<Vehicule>    @GET("vehicules/{id}")    fun getVehicule(@Header("Authorization") token: String, @Path("id") id: Int): Call<Vehicule>    @PUT("vehicules/{id}")    fun updateVehicule(@Header("Authorization") token: String, @Path("id") id: Int, @Body vehicule: Vehicule): Call<Vehicule>    @DELETE("vehicules/{id}")    fun deleteVehicule(@Header("Authorization") token: String, @Path("id") id: Int): Call<Void>}